import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import styled from "styled-components";

interface IArrow {
  margin: string;
}
const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;

  align-items: center;
  justify-content: space-between;
`;

const Arrow = styled.div<IArrow>`
  width: 50px;
  height: 50px;
  background-color: #fff7f7;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  opacity: 0.5;
  margin-left: ${(props) => props.margin === "left" && "10px"};
  margin-right: ${(props) => props.margin === "right" && "10px"};
`;

const Wrapper = styled.div
const Slider = () => {
  return (
    <Container>
      <Arrow margin="left">
        <ArrowLeftOutlined />
      </Arrow>
      <Arrow margin="right">
        <ArrowRightOutlined />
      </Arrow>
    </Container>
  );
};

export default Slider;
