import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import styled from "styled-components";
import WomanImage from "../../assets/images/main-woman.jpg";

interface IArrow {
  margin: string;
}

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;

  align-items: center;
  justify-content: space-between;
`;

const Arrow = styled.div<IArrow>`
  width: 50px;
  height: 50px;
  background-color: #fff7f7;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  opacity: 0.5;
  margin-left: ${(props) => props.margin === "left" && "10px"};
  margin-right: ${(props) => props.margin === "right" && "10px"};
`;

const Wrapper = styled.div`
  height: 100%;
`;

const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
`;
const ImgContainer = styled.div`
  height: 100%;
  flex: 1;
`;

const Image = styled.img`
  height: 80%;
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 50px;
`;

const Title = styled.h2``;
const Description = styled.p``;
const Button = styled.button``;

const Slider = () => {
  console.log(WomanImage);
  return (
    <Container>
      <Arrow margin="left">
        <ArrowLeftOutlined />
      </Arrow>
      <Wrapper>
        <Slide>
          <ImgContainer>
            <Image src={WomanImage} />
          </ImgContainer>
          <InfoContainer>
            <Title>Summer Sale</Title>
            <Description>
              DON'T COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS.
            </Description>
            <Button></Button>
          </InfoContainer>
        </Slide>
      </Wrapper>
      <Arrow margin="right">
        <ArrowRightOutlined />
      </Arrow>
    </Container>
  );
};

export default Slider;
